import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export default function ProductDetails() {
    const [product, setProduct] = useState(null); // Use singular 'product'
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        const fetchProduct = async () => {
            setLoading(true);
            setError(null);
            try {
                const res = await fetch(process.env.REACT_APP_API_URL + '/products/' + id);
                if (!res.ok) {
                    throw new Error(`HTTP error! status: ${res.status}`);
                }
                const data = await res.json();
                if (data.products && data.products.length > 0) {
                    setProduct(data.products[0]);
                } else if (data) {
                    setProduct(data);
                } else {
                    throw new Error("No product data received");
                }
            } catch (err) {
                setError(err);
                console.error("Error fetching product:", err);
            } finally {
                setLoading(false);
            }
        };

        fetchProduct();
    }, [id]); // Include 'id' in the dependency array

    if (loading) {
        return <div>Loading product details...</div>;
    }

    if (error) {
        return <div>Error: {error.message}</div>;
    }

    if (!product) {
        return <div>Product not found.</div>; // Handle the case where product is still null after loading
    }

    return (
        <div className="container container-fluid">
            <div className="row f-flex justify-content-around">
                <div className="col-12 col-lg-5 img-fluid" id="product_image">
                    {product.images && product.images.length > 0 ? (
                        <img src={product.images[0].image} alt={product.name || "Product Image"} height="500" width="500" />
                    ) : (
                        <div>No image available</div>
                    )}
                </div>
                <div className="col-12 col-lg-5 mt-5">
                    <h3>{product.name || "Product Name"}</h3> {/* Use product name */}
                    <p id="product_id">Product # {product.id || "Product ID"}</p> {/* Use product ID */}
                    <hr />
                    {/* ... rest of your JSX using product.* ... */}
                    <p id="product_price">${product.price || "Price"}</p>
                    <p>Status: <span id="stock_status">{product.stock > 0 ? "In Stock" : "Out of Stock"}</span></p>
                    <h4 className="mt-2">Description:</h4>
                    <p>{product.description || "Product Description"}</p>
                    <p id="product_seller mb-3">Sold by: <strong>{product.seller || "Seller"}</strong></p>
                </div>
            </div>
        </div>
    );
}
